<?php defined('IN_IA') or exit('Access Denied');?><?php include page("header-base");?><script language="javascript">require(['underscore']);</script><!-- 导入CSS样式 --><link href="<?php echo RESOURCE_ROOT;?>eshop/static/designer/imgsrc/designer.css" rel="stylesheet"><!-- 头部选项卡 --><?php  if($op=='display') { ?><!-- 筛选区域 --><div class="panel ">          <h3 class="custom_page_header">  店铺装修  <a class='btn btn-default' href="<?php  echo $this->createWebUrl('designer', array('op' => 'post','act'=>'index'))?>"><i class="fa fa-plus"></i> 添加一个新页面</a>                                  </h3>      <div class='panel-body'>        <table class="table">            <thead>                <tr>                    <th style="text-align: center;">页面名称</th>                    <th style="width:200px;">最后修改时间</th>                    <th style="text-align: center;">页面类型</th>                    <th style="text-align: center;">操作</th>                </tr>            </thead>            <tbody>                <?php  if(!empty($pages)) { ?>                    <?php  if(is_array($pages)) { foreach($pages as $page) { ?>                        <tr pageid="<?php  echo $page['id'];?>">                            <td style="text-align: center;"><?php  echo $page['pagename'];?></td>                                                        <td style=" text-align:  center;"><?php  echo $page['savetime'];?></td>                            <td style="text-align:  center;" ">                            	  <?php  if(empty($page['pagetype'])) { ?>                                      <label class='label label-primary' >普通界面</label>                                                                       <?php  } else { ?>                                   <label class='label label-success'  >商城首页</label>                                                                   <?php  } ?>                            </td>                            <td style=" text-align:  center;">                                <a href="<?php  echo create_url('mobile',array('act'=>'shopwap','do'=>'diypage'))?>&pageid=<?php  echo $page['id'];?>" target="_blank">预览</a> -                                - <a href="<?php  echo $this->createWebUrl('designer',array('op'=>'post','pageid'=>$page['id']))?>">编辑</a>                                - <a href="javascript:;" onclick="delpage(<?php  echo $page['id'];?>)">删除</a>                            </td>                        </tr>                    <?php  } } ?>                <?php  } else { ?>                                  <tr>                         <td style='text-align: center;padding:30px;' colspan="8">暂时没有自定义页面</td>                    </tr>                              <?php  } ?>                           </tbody>        </table><?php  echo $pager;?>    </div></div>     <script type="text/javascript">    function delpage(id){        if(confirm('此操作不可恢复，确认删除？')){             $.ajax({                type: 'POST',                url: "<?php  echo $this->createWebUrl('designer',array('op'=>'api','apido'=>'delpage'))?>",                data: {pageid:id},                success: function(data){                    if(data=='success'){                        $("tr[pageid="+id+"]").fadeOut();                    }                    else{                        alert(data);                    }                },                error: function(){                    alert('操作失败~请刷新页面重试！');                }            });        }    }    function setdefault(t,id,type){        thisdo = $(t).data("do");        d = thisdo;        $.ajax({            type: 'POST',            dataType:'json',            url: "<?php  echo $this->createWebUrl('designer',array('op'=>'api','apido'=>'setdefault'))?>",            data: {d:d,id:id,type:type},            success: function(data){                if(data['result']=='on'){                    $("td[data-id="+data['id']+"]").find("label").data("do","off").removeClass("label-default").addClass("label-success").text("商城首页").attr("title","点击修改成普通页面");                    $("td[data-id="+data['closeid']+"]").find("label").data("do","on").removeClass("label-success").addClass("label-default").text("普通页面").attr("title","点击修改成商城首页");                }else{                    $("td[data-id="+data['id']+"]").find("label").data("do","on").removeClass("label-success").addClass("label-default").text("普通页面").attr("title","点击修改成商城首页");                }            },            error: function(){                alert('操作失败~请刷新页面重试！');            }        });    }</script><?php  } ?><?php include page("footer-base");?>